mapper.select:
select c.id,c.fullname,d1.value AS appellation,u1.NAME AS OWNER,c.company,c.job,c.email,c.phone,c.website,c.mphone,d2.VALUE AS state,d3.value AS source,u2.NAME AS create_by,c.create_time,c.description,c.contact_summary,c.next_contact_time,c.address,u3.NAME AS edit_by,c.edit_time FROM tbl_clue c JOIN tbl_dic_value d1 ON c.appellation = d1.id JOIN tbl_user u1 ON c.OWNER = u1.id JOIN tbl_dic_value d2 ON c.state = d2.id JOIN tbl_dic_value d3 ON c.source = d3.id JOIN tbl_user u2 ON c.create_by = u2.id LEFT JOIN tbl_user u3 ON c.edit_by = u3.id
and fullname like '%' #{fullname} '%'
and owner = #{owner}
and company like '%' #{company} '%'
and mphone like '%' #{mphone} '%'
and state = #{state}
and phone like '%' #{phone} '%'
and source = #{source}
order by create_time desc limit #{beginNo},#{pageSize}

select count( *) FROM tbl_clue c JOIN tbl_dic_value d1 ON c.appellation = d1.id JOIN tbl_user u1 ON c.OWNER = u1.id JOIN tbl_dic_value d2 ON c.state = d2.id JOIN tbl_dic_value d3 ON c.source = d3.id JOIN tbl_user u2 ON c.create_by = u2.id LEFT JOIN tbl_user u3 ON c.edit_by = u3.id
and fullname like '%' #{fullname} '%'
and owner = #{owner}
and company like '%' #{company} '%'
and mphone like '%' #{mphone} '%'
and state = #{state}
and phone like '%' #{phone} '%'
and source = #{source}

mapper.delete:
delete from tbl_clue
    where id in

      #{id}

mapper.update:
update tbl_clue
set  fullname =#{fullname}, appellation=#{appellation},owner =#{owner}, company =#{company},
job= #{job},email =#{email},phone =#{phone},website =#{website},mphone = #{mphone}, state =#{state},
source =#{source},description =#{description},contact_summary = #{contactSummary},
next_contact_time = #{nextContactTime},address =#{address},edit_by =#{editBy},edit_time =#{editTime}
where id =#{id}
